INSERT INTO games (name, description, cover, votes) VALUES ('A Plague Tale: Requiem','Six months following the events of the first game, siblings Amicia and Hugo de Rune, along with their mother Beatrice and friend Lucas, are on the road to Provence. An organization of alchemists called the Order has promised to provide refuge to the de Rune family. However, they encounter a hostile family of beekeepers who attempted to kill him, Amicia, and the youngest sibling of that family, forcing him to call the rats to save all three of them.', 'https://images.igdb.com/igdb/image/upload/t_cover_big/co5pwb.png', 0);
INSERT INTO games (name, description, cover, votes) VALUES ('Elden Ring', 'Elden Ring takes place in the Lands Between, a fictional landmass ruled over by several demigods. It was previously ruled over by the immortal Queen Marika, who acted as keeper of the Elden Ring, a powerful force that manifested as the physical concept of order. When Marika eventually shattered the Elden Ring and disappeared, her demigod children began warring over pieces of the Ring in an event called the Shattering.', 'https://images.igdb.com/igdb/image/upload/t_cover_big/co4jni.png', 0);
INSERT INTO games (name, description, cover, votes) VALUES ('God of War: Ragnarok', 'The game begins as Fimbulwinter nears its end. Kratos and Atreus return home from a hunting trip—fending off an ambush from a vengeful Freya—only to find their wolf Fenrir in his final moments.', 'https://images.igdb.com/igdb/image/upload/t_cover_big/co5s5v.png', 0);
INSERT INTO games (name, description, cover, votes) VALUES ('Horizon: Forbidden West', 'In the six months following the defeat of HADES (Anthony Ingruber) in Horizon Zero Dawn, Aloy has been searching fruitlessly for a working backup of GAIA (Lesley Ewen) to restore the planet rapidly degrading biosphere.', 'https://images.igdb.com/igdb/image/upload/t_cover_big/co2gvu.png', 0);
INSERT INTO games (name, description, cover, votes) VALUES ('Stray', 'The story follows a stray cat who falls into a walled city populated by robots, machines, and mutant bacteria, and sets out to return to the surface with the help of a drone companion, B-12. The game is presented through a third-person perspective.', 'https://images.igdb.com/igdb/image/upload/t_cover_big/co4tt2.png', 0);
INSERT INTO games (name, description, cover, votes) VALUES ('Xenoblade Chronicles 3', 'The story follows Noah and his two childhood friends from Keves, and Mio and her two fellow servicemen from Agnus, who all gain a mysterious power and decide to cooperate to find safety. Through this journey, they uncover the mystery behind this perpetual war and the nature of their world.', 'https://images.igdb.com/igdb/image/upload/t_cover_big/co4ocq.png', 0);
INSERT INTO games (name, description, cover, votes) VALUES ('Immortality', 'The game is based on the fictional model turned actress Marissa Marcel (Manon Gage) who had starred in three movies from 1968, 1970, and 1999 but which were never released. Marcel has since gone missing, creating a mystery for the player to solve.', 'https://images.igdb.com/igdb/image/upload/t_cover_big/co55cl.png', 0);
INSERT INTO games (name, description, cover, votes) VALUES ('Call of Duty: Modern Warfare 2', 'The story follows Task Force 141, a multi-national special forces unit commanded by Captain Soap MacTavish as they hunt Vladimir Makarov, leader of the Russian Ultranationalist party, and U.S. Army Rangers from 1st Battalion/75th Ranger Regiment who are defending the Washington, D.C. area from a Russian invasion.', 'https://images.igdb.com/igdb/image/upload/t_cover_big/co4sbw.png', 0);
INSERT INTO games (name, description, cover, votes) VALUES ('Pokémon Legends: Arceus', 'The game follows the protagonist, sent back through time, as they travel through the Hisui region based on the island of Hokkaido during early Japanese colonization.', 'https://images.igdb.com/igdb/image/upload/t_cover_big/co3d03.png', 0);
INSERT INTO games (name, description, cover, votes) VALUES ('Gran Turismo 7', 'Gran Turismo 7 is a racing simulation video game developed by Polyphony Digital and published by Sony Interactive Entertainment.', 'https://images.igdb.com/igdb/image/upload/t_cover_big/co2g84.png', 0);
